mapper.delete:
delete review_board
 where rbrd_num = #{rbrd_num}
 	and mem_id = #{mem_id}

mapper.select:
select mem_id
	from review_board
		where rbrd_num = #{rbrd_num}

mapper.update:
update review_board
	set
		rbrd_content = #{rbrd_content}
	where rbrd_num = #{rbrd_num}
		and mem_id = #{mem_id}

mapper.delete:
delete item_wishlist
	where wishlist_num = #{wishlist_num}
		and mem_id = #{mem_id}
